import React, { useEffect, useCallback, useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { InjectedConnector } from '@web3-react/injected-connector';
import { ethers } from 'ethers';  // ethers 라이브러리 import 추가
import TokenExchange from './components/TokenExchange';
import './App.css';

const SEPOLIA_CHAIN_ID = 11155111;

const injected = new InjectedConnector({
  supportedChainIds: [SEPOLIA_CHAIN_ID]
});

function App() {
  const { active, account, activate, deactivate, chainId, library } = useWeb3React();
  const [isConnecting, setIsConnecting] = useState(false);

  useEffect(() => {
    if (active && library && chainId !== SEPOLIA_CHAIN_ID) {
      checkAndSwitchNetwork();
    }
  }, [active, chainId, library]);

  const handleAccountsChanged = useCallback((accounts) => {
    if (accounts.length > 0 && account !== accounts[0]) {
      activate(injected);
    } else {
      deactivate();
    }
  }, [account, activate, deactivate]);

  useEffect(() => {
    const { ethereum } = window;
    if (ethereum && ethereum.on) {
      ethereum.on('accountsChanged', handleAccountsChanged);
    }

    return () => {
      if (ethereum && ethereum.removeListener) {
        ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, [handleAccountsChanged]);

  async function connect() {
    if (isConnecting) return;
    setIsConnecting(true);
    try {
      console.log('Activating injected connector...');
      await activate(injected);
      console.log('Injected connector activated');
      
      console.log('Waiting for library initialization...');
      let attempts = 0;
      while (!library && attempts < 100) {
        await new Promise(resolve => setTimeout(resolve, 100));
        attempts++;
      }

      if (!library) {
        throw new Error('Library initialization timeout');
      }
      console.log('Library initialized:', library);

      const provider = new ethers.providers.Web3Provider(library.provider);
      const network = await provider.getNetwork();
      console.log('Network:', network);
      const connectedChainId = network.chainId;
      console.log('Connected chainId:', connectedChainId);
      
      if (connectedChainId !== SEPOLIA_CHAIN_ID) {
        console.log('Switching to Sepolia network...');
        const switched = await checkAndSwitchNetwork();
        if (!switched) {
          console.log('Failed to switch to Sepolia');
          alert("This application requires Sepolia network. Please switch to Sepolia and try connecting again.");
          await deactivate();
        } else {
          console.log('Successfully switched to Sepolia');
        }
      } else {
        console.log('Already on Sepolia network');
      }
    } catch (ex) {
      console.error('Failed to connect:', ex);
      if (ex.name === 'NoEthereumProviderError') {
        alert("No Ethereum wallet detected. Please install a wallet extension or use a dApp browser.");
      } else if (ex.name === 'UnsupportedChainIdError') {
        alert("You're connected to an unsupported network.");
      } else {
        alert("An unknown error occurred. Check the console for more details.");
      }
      await deactivate();
    } finally {
      setIsConnecting(false);
    }
  }

  async function disconnect() {
    try {
      deactivate();
    } catch (ex) {
      console.error('Failed to disconnect:', ex);
    }
  }

  async function switchToSepolia() {
    const { ethereum } = window;
    if (!ethereum) {
      console.error("No Ethereum provider found");
      return false;
    }

    try {
      await ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: `0x${SEPOLIA_CHAIN_ID.toString(16)}` }],
      });
      return true;
    } catch (switchError) {
      if (switchError.code === 4902) {
        try {
          await ethereum.request({
            method: "wallet_addEthereumChain",
            params: [
              {
                chainId: `0x${SEPOLIA_CHAIN_ID.toString(16)}`,
                chainName: "Sepolia Test Network",
                rpcUrls: ["https://rpc.sepolia.org"],
                nativeCurrency: {
                  name: "Sepolia Ether",
                  symbol: "SEP",
                  decimals: 18
                },
                blockExplorerUrls: ["https://sepolia.etherscan.io"]
              }
            ],
          });
          return true;
        } catch (addError) {
          console.error('Failed to add Sepolia network:', addError);
        }
      } else {
        console.error('Failed to switch to Sepolia network:', switchError);
      }
    }
    return false;
  }

  const checkAndSwitchNetwork = async () => {
    const { ethereum } = window;
    if (!ethereum) {
      console.error("No Ethereum provider found");
      return false;
    }

    const provider = new ethers.providers.Web3Provider(ethereum);
    const network = await provider.getNetwork();
    
    if (network.chainId !== SEPOLIA_CHAIN_ID) {
      const userConfirmed = window.confirm("This application requires Sepolia network. Do you want to switch to Sepolia?");
      if (userConfirmed) {
        const switched = await switchToSepolia();
        if (!switched) {
          alert("Failed to switch to Sepolia network. Please switch manually in your wallet and try again.");
        }
        return switched;
      } else {
        return false;
      }
    }
    return true;
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="placeholder"></div>
          <h1 className="logo-container">
            <img 
              src="/gpex_logo_whtie_1000px.webp" 
              alt="GPEX Token Exchange" 
              className="logo" 
              style={{
                width: '200px',
                height: 'auto'
              }}
            />
          </h1>
          <div className="wallet-buttons">
            {active ? (
              <>
                <button className="wallet-address-button">
                  {account.substring(0, 4)}...{account.substring(account.length - 4)}
                </button>
                <button onClick={disconnect} className="disconnect-button">
                  Disconnect
                </button>
                {chainId !== SEPOLIA_CHAIN_ID && (
                  <button onClick={checkAndSwitchNetwork} className="switch-network-button">
                    Switch to Sepolia
                  </button>
                )}
              </>
            ) : (
              <button onClick={connect} disabled={isConnecting} className="connect-wallet-button">
                {isConnecting ? 'Connecting...' : 'Connect Wallet'}
              </button>
            )}
          </div>
        </div>
      </header>
      <main className="content-container">
        <TokenExchange checkAndSwitchNetwork={checkAndSwitchNetwork} />
      </main>
    </div>
  );
}

export default App;
