import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { ethers } from 'ethers';
import { SEPOLIA_CHAIN_ID, CONTRACT_ADDRESS, CONTRACT_ABI } from '../App';

const ERC20_ABI = [
  // 다른 함수들...
  {
    constant: true,
    inputs: [{ name: "_owner", type: "address" }],
    name: "balanceOf",
    outputs: [{ name: "balance", type: "uint256" }],
    type: "function",
  },
  // 다른 함수들...
];

const TOKENS = {
  ETH: { symbol: 'ETH', name: 'Ethereum', decimals: 18 },
  gUSDC: { symbol: 'gUSDC', name: 'gUSDC', address: '0x37B118a528AAD5a22259AFfa21c34254fDa2B60c', decimals: 18 },
  gUSDT: { symbol: 'gUSDT', name: 'gUSDT', address: '0xeB06441f880F5948e80Ae62042687b4dE22124f2', decimals: 18 },
  gKIP: { symbol: 'gKIP', name: 'gKIP', address: '0x4B5bA223e2f5e4c746C5F714fAD6D641Ef195a5B', decimals: 18 },
};

function TokenExchange({ checkAndSwitchNetwork, switchToSepolia }) {
  const { active, library, account, chainId } = useWeb3React();
  const [inputToken, setInputToken] = useState('ETH');
  const [outputToken, setOutputToken] = useState('gKIP');
  const [inputAmount, setInputAmount] = useState('');
  const [outputAmount, setOutputAmount] = useState('');
  const [prices, setPrices] = useState({});
  const [balances, setBalances] = useState({});
  const [error, setError] = useState('');
  const [lastPriceUpdate, setLastPriceUpdate] = useState(0);

  useEffect(() => {
    if (active && library) {
      checkAndSwitchNetwork().then((result) => {
        if (result) {
          updateBalances();
        }
      });
    }
  }, [active, library, account, chainId, checkAndSwitchNetwork]);

  useEffect(() => {
    calculateOutputAmount();
  }, [inputAmount, inputToken, outputToken, prices]);

  const fetchPrices = async () => {
    try {
      const response = await fetch('https://api.coingecko.com/api/v3/simple/price?ids=ethereum,usd-coin,tether&vs_currencies=usd');
      const data = await response.json();
      const newPrices = {
        ETH: data.ethereum.usd,
        gUSDC: data['usd-coin'].usd,
        gUSDT: data.tether.usd,
        gKIP: 0.00012, // gKIP 가격은 여전히 고정값 사용
      };
      setPrices(newPrices);
      setLastPriceUpdate(Date.now());

      return newPrices;
    } catch (error) {
      console.error('Failed to fetch prices:', error);
      throw error;
    }
  };

  const calculateOutputAmount = () => {
    if (!inputAmount || !prices[inputToken] || !prices[outputToken]) {
      setOutputAmount('0.0000');
      return;
    }
    const inputValue = parseFloat(inputAmount) * prices[inputToken];
    const outputValue = inputValue / prices[outputToken];
    setOutputAmount(outputValue.toFixed(6));
  };

  const handleInputTokenChange = async (e) => {
    if (await checkAndSwitchNetwork()) {
      setInputToken(e.target.value);
    }
  };

  const handleOutputTokenChange = async (e) => {
    if (await checkAndSwitchNetwork()) {
      setOutputToken(e.target.value);
    }
  };

  const handleExchange = async () => {
    if (!await checkAndSwitchNetwork()) {
      return;
    }

    if (!active || !library) {
      alert('Please connect your wallet first');
      return;
    }

    if (parseFloat(inputAmount) > parseFloat(balances[inputToken])) {
      setError('Insufficient balance');
      return;
    }

    setError('');

    try {
      // 교환 직전에 가격 업데이트
      const latestPrices = await fetchPrices();

      const signer = library.getSigner();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);

      // 스마트 컨트랙트에 가격 업데이트
      await contract.updatePrices(
        ethers.utils.parseUnits(latestPrices.ETH.toString(), 18),
        ethers.utils.parseUnits(latestPrices.gUSDC.toString(), 18),
        ethers.utils.parseUnits(latestPrices.gUSDT.toString(), 18),
        ethers.utils.parseUnits(latestPrices.gKIP.toString(), 18)
      );

      let tx;
      if (inputToken === 'ETH') {
        tx = await contract.exchangeETHForToken(TOKENS[outputToken].address, {
          value: ethers.utils.parseEther(inputAmount)
        });
      } else {
        const inputTokenContract = new ethers.Contract(TOKENS[inputToken].address, ERC20_ABI, signer);
        await inputTokenContract.approve(CONTRACT_ADDRESS, ethers.utils.parseUnits(inputAmount, TOKENS[inputToken].decimals));
        tx = await contract.exchangeTokenForToken(
          TOKENS[inputToken].address,
          TOKENS[outputToken].address,
          ethers.utils.parseUnits(inputAmount, TOKENS[inputToken].decimals)
        );
      }

      await tx.wait();
      alert('Exchange successful!');
      updateBalances();
    } catch (error) {
      console.error('Exchange failed:', error);
      if (error.code === 4001) {
        alert('Transaction was rejected by the user');
      } else if (error.message.includes('insufficient funds')) {
        alert('Insufficient funds for this transaction');
      } else {
        alert('Exchange failed. Please check console for details.');
      }
    }
  };

  const updateBalances = async () => {
    if (!active || !library || !account) return;

    const newBalances = {};
    for (const [symbol, token] of Object.entries(TOKENS)) {
      if (symbol === 'ETH') {
        const balance = await library.getBalance(account);
        newBalances[symbol] = ethers.utils.formatEther(balance);
      } else {
        try {
          const tokenContract = new ethers.Contract(token.address, ERC20_ABI, library.getSigner());
          const balance = await tokenContract.balanceOf(account);
          newBalances[symbol] = ethers.utils.formatUnits(balance, token.decimals);
        } catch (error) {
          console.error(`Error fetching balance for ${symbol}:`, error);
          newBalances[symbol] = '0';
        }
      }
    }
    setBalances(newBalances);
  };

  const setMaxAmount = () => {
    if (inputToken === 'ETH') {
      // ETH의 경우 가스비를 위해 약간의 여유를 둡니다.
      const maxAmount = parseFloat(balances[inputToken]) - 0.01;
      setInputAmount(maxAmount > 0 ? maxAmount.toString() : '0');
    } else {
      setInputAmount(balances[inputToken]);
    }
  };

  return (
    <div className="exchange-container">
      <h2>Swap</h2>
      <div className="swap-box">
        <div className="token-input">
          <div className="token-select">
            <select 
              value={inputToken} 
              onChange={handleInputTokenChange}
            >
              {Object.keys(TOKENS).map(token => (
                <option key={token} value={token}>{TOKENS[token].name}</option>
              ))}
            </select>
          </div>
          <input
            type="number"
            value={inputAmount}
            onChange={(e) => {
              setInputAmount(e.target.value);
              setError('');
            }}
            placeholder="0.0"
          />
          <button onClick={setMaxAmount} className="max-button">MAX</button>
        </div>
        <p className="balance">Balance: {active ? parseFloat(balances[inputToken]).toFixed(4) : '0.0000'} {inputToken}</p>
        
        <div className="swap-icon">↓</div>
        
        <div className="token-input">
          <div className="token-select">
            <select 
              value={outputToken} 
              onChange={handleOutputTokenChange}
            >
              {Object.keys(TOKENS).filter(token => token !== inputToken).map(token => (
                <option key={token} value={token}>{TOKENS[token].name}</option>
              ))}
            </select>
          </div>
          <input
            type="number"
            value={outputAmount || '0.0000'}
            readOnly
            placeholder="0.0000"
          />
        </div>
        <p className="balance">Balance: {parseFloat(balances[outputToken] || '0').toFixed(4)} {outputToken}</p>
      </div>
      
      {error && <p className="error-message">{error}</p>}
      
      <button 
        onClick={handleExchange} 
        disabled={!active || !inputAmount || parseFloat(inputAmount) > parseFloat(balances[inputToken])}
        className="swap-button"
      >
        Swap
      </button>
      
      <p className="price-update">Last price update: {new Date(lastPriceUpdate).toLocaleTimeString()}</p>
    </div>
  );
}

export default TokenExchange;
