import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { ethers } from 'ethers';

const CONTRACT_ADDRESS = '0x...'; // 새로 배포한 컨트랙트 주소
const CONTRACT_ABI = [/* 새로운 ABI */];
const ERC20_ABI = [/* ERC20 토큰 ABI */];

const TOKENS = {
  ETH: { symbol: 'ETH', name: 'Ethereum', decimals: 18 },
  gUSDC: { symbol: 'gUSDC', name: 'gUSDC', address: '0x...', decimals: 6 },
  gUSDT: { symbol: 'gUSDT', name: 'gUSDT', address: '0x...', decimals: 6 },
  gKIP: { symbol: 'gKIP', name: 'gKIP', address: '0x...', decimals: 18 },
};

function TokenExchange() {
  const { active, library, account } = useWeb3React();
  const [inputToken, setInputToken] = useState('ETH');
  const [outputToken, setOutputToken] = useState('gKIP');
  const [inputAmount, setInputAmount] = useState('');
  const [outputAmount, setOutputAmount] = useState('');
  const [prices, setPrices] = useState({});
  const [balances, setBalances] = useState({});

  useEffect(() => {
    if (active && library) {
      updateBalances();
      fetchPrices();
    }
  }, [active, library, account, inputToken]);

  useEffect(() => {
    calculateOutputAmount();
  }, [inputAmount, inputToken, outputToken, prices]);

  const fetchPrices = async () => {
    try {
      const response = await fetch('https://api.coingecko.com/api/v3/simple/price?ids=ethereum,usd-coin,tether&vs_currencies=usd');
      const data = await response.json();
      setPrices({
        ETH: data.ethereum.usd,
        gUSDC: data['usd-coin'].usd,
        gUSDT: data.tether.usd,
        gKIP: 0.00012, // Laos Kip 가격은 별도로 찾아야 할 수 있습니다.
      });
    } catch (error) {
      console.error('Failed to fetch prices:', error);
    }
  };

  const calculateOutputAmount = () => {
    if (!inputAmount || !prices[inputToken] || !prices[outputToken]) return;
    const inputValue = parseFloat(inputAmount) * prices[inputToken];
    const outputValue = inputValue / prices[outputToken];
    setOutputAmount(outputValue.toFixed(6));
  };

  const handleExchange = async () => {
    if (!active || !library) {
      alert('Please connect your wallet first');
      return;
    }

    try {
      const signer = library.getSigner();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);

      let tx;
      if (inputToken === 'ETH') {
        tx = await contract.exchangeETHForToken(TOKENS[outputToken].address, {
          value: ethers.utils.parseEther(inputAmount)
        });
      } else {
        const inputTokenContract = new ethers.Contract(TOKENS[inputToken].address, ERC20_ABI, signer);
        await inputTokenContract.approve(CONTRACT_ADDRESS, ethers.utils.parseUnits(inputAmount, TOKENS[inputToken].decimals));
        tx = await contract.exchangeTokenForToken(
          TOKENS[inputToken].address,
          TOKENS[outputToken].address,
          ethers.utils.parseUnits(inputAmount, TOKENS[inputToken].decimals)
        );
      }

      await tx.wait();
      alert('Exchange successful!');
      updateBalances();
    } catch (error) {
      console.error('Exchange failed:', error);
      alert('Exchange failed. Please check console for details.');
    }
  };

  const updateBalances = async () => {
    if (!active || !library || !account) return;

    const newBalances = {};
    for (const [symbol, token] of Object.entries(TOKENS)) {
      if (symbol === 'ETH') {
        const balance = await library.getBalance(account);
        newBalances[symbol] = ethers.utils.formatEther(balance);
      } else {
        const tokenContract = new ethers.Contract(token.address, ERC20_ABI, library);
        const balance = await tokenContract.balanceOf(account);
        newBalances[symbol] = ethers.utils.formatUnits(balance, token.decimals);
      }
    }
    setBalances(newBalances);
  };

  const setMaxAmount = () => {
    const maxAmount = balances[inputToken];
    if (inputToken === 'ETH') {
      // ETH의 경우 가스비를 위해 약간의 여유를 둡니다.
      const adjustedMaxAmount = parseFloat(maxAmount) - 0.01;
      setInputAmount(adjustedMaxAmount > 0 ? adjustedMaxAmount.toString() : '0');
    } else {
      setInputAmount(maxAmount);
    }
  };

  return (
    <div className="exchange-container">
      <h2>Token Exchange</h2>
      <div>
        <select value={inputToken} onChange={(e) => setInputToken(e.target.value)}>
          {Object.keys(TOKENS).map(token => (
            <option key={token} value={token}>{TOKENS[token].name}</option>
          ))}
        </select>
        <input
          type="number"
          value={inputAmount}
          onChange={(e) => setInputAmount(e.target.value)}
          placeholder="Input Amount"
        />
        <button onClick={setMaxAmount}>Max</button>
        <p>Balance: {balances[inputToken] || '0'} {inputToken}</p>
      </div>
      <div>
        <select value={outputToken} onChange={(e) => setOutputToken(e.target.value)}>
          {Object.keys(TOKENS).filter(token => token !== inputToken).map(token => (
            <option key={token} value={token}>{TOKENS[token].name}</option>
          ))}
        </select>
        <input
          type="number"
          value={outputAmount}
          readOnly
          placeholder="Output Amount"
        />
      </div>
      <button onClick={handleExchange} disabled={!active || !inputAmount}>
        Exchange
      </button>
    </div>
  );
}

export default TokenExchange;
