import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { ethers } from 'ethers';

const CONTRACT_ADDRESS = '0x779b95281AdBB3DAE84E7fF4Ad3A0A3bD475235e';
const CONTRACT_ABI = [
  {
    "inputs": [],
    "name": "exchangeETHForGKIP",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  },
  // ... 다른 함수들의 ABI도 포함해야 합니다.
];

function TokenExchange() {
  const { active, library, account } = useWeb3React();
  const [amount, setAmount] = useState('');
  const [balance, setBalance] = useState('0');

  useEffect(() => {
    if (active && library) {
      updateBalance();
    }
  }, [active, library, account]);

  const updateBalance = async () => {
    const balance = await library.getBalance(account);
    setBalance(ethers.utils.formatEther(balance));
  };

  const handleExchange = async () => {
    if (!active || !library) {
      alert('Please connect your wallet first');
      return;
    }

    try {
      const signer = library.getSigner();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);

      // ETH를 Wei로 변환
      const amountInWei = ethers.utils.parseEther(amount);

      // 트랜잭션 실행
      const tx = await contract.exchangeETHForGKIP({ value: amountInWei });
      
      // 트랜잭션 완료 대기
      await tx.wait();

      alert('Exchange successful!');
      updateBalance();
    } catch (error) {
      console.error('Exchange failed:', error);
      alert('Exchange failed. Please check console for details.');
    }
  };

  const setMaxAmount = () => {
    // 가스비를 위해 약간의 ETH를 남겨둡니다 (0.01 ETH)
    const maxAmount = parseFloat(balance) - 0.01;
    setAmount(maxAmount > 0 ? maxAmount.toString() : '0');
  };

  return (
    <div className="exchange-container">
      <h2>Exchange ETH for gKIP</h2>
      <p>Your ETH Balance: {parseFloat(balance).toFixed(4)} ETH</p>
      <div className="input-container">
        <button onClick={setMaxAmount} className="max-button">Max</button>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount in ETH"
          className="amount-input"
        />
      </div>
      <button 
        onClick={handleExchange} 
        disabled={!active || parseFloat(amount) <= 0}
        className="exchange-button"
      >
        Exchange
      </button>
      {active && <p>Connected account: {account}</p>}
    </div>
  );
}

export default TokenExchange;
