import React from 'react';
import { useWeb3React } from '@web3-react/core';
import { InjectedConnector } from '@web3-react/injected-connector';
import TokenExchange from './components/TokenExchange';
import './App.css';

const injected = new InjectedConnector({
  supportedChainIds: [1, 3, 4, 5, 42] // 지원하는 네트워크 ID
});

function App() {
  const { active, account, activate, deactivate } = useWeb3React();

  async function connect() {
    try {
      await activate(injected);
    } catch (ex) {
      console.log(ex);
    }
  }

  async function disconnect() {
    try {
      deactivate();
    } catch (ex) {
      console.log(ex);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="wallet-button">
            {active ? (
              <button onClick={disconnect} className="connect-wallet-button">
                Disconnect: {account.substring(0, 6)}...{account.substring(account.length - 4)}
              </button>
            ) : (
              <button onClick={connect} className="connect-wallet-button">
                Connect Wallet
              </button>
            )}
          </div>
          <h1 className="logo-container">
            <img 
              src="/gpex_logo_whtie_1000px.webp" 
              alt="GPEX Token Exchange" 
              className="logo" 
              style={{
                width: '200px',
                height: 'auto'
              }}
            />
          </h1>
          <div className="placeholder"></div>
        </div>
      </header>
      <main className="content-container">
        <TokenExchange />
      </main>
    </div>
  );
}

export default App;
