import React, { useEffect, useCallback } from 'react';
import { useWeb3React } from '@web3-react/core';
import { InjectedConnector } from '@web3-react/injected-connector';
import TokenExchange from './components/TokenExchange';
import './App.css';

const SEPOLIA_CHAIN_ID = 11155111;

const injected = new InjectedConnector({
  supportedChainIds: [SEPOLIA_CHAIN_ID]
});

function App() {
  const { active, account, activate, deactivate, chainId, library } = useWeb3React();

  const handleAccountsChanged = useCallback((accounts) => {
    if (accounts.length > 0 && account !== accounts[0]) {
      // 새 계정으로 상태 업데이트
      activate(injected);
    } else {
      // 모든 계정 연결 해제
      deactivate();
    }
  }, [account, activate, deactivate]);

  useEffect(() => {
    if (active && chainId !== SEPOLIA_CHAIN_ID) {
      switchToSepolia();
    }
  }, [active, chainId]);

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
    }

    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, [handleAccountsChanged]);

  async function connect() {
    try {
      await activate(injected);
    } catch (ex) {
      console.error('Failed to connect:', ex);
    }
  }

  async function disconnect() {
    try {
      deactivate();
    } catch (ex) {
      console.error('Failed to disconnect:', ex);
    }
  }

  async function switchToSepolia() {
    if (!active || !library || !library.provider) {
      console.error("Wallet is not connected. Please connect your wallet first.");
      return false;
    }

    const provider = library.provider;

    try {
      await provider.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: `0x${SEPOLIA_CHAIN_ID.toString(16)}` }],
      });
      return true;
    } catch (switchError) {
      // This error code indicates that the chain has not been added to MetaMask.
      if (switchError.code === 4902) {
        try {
          await provider.request({
            method: "wallet_addEthereumChain",
            params: [
              {
                chainId: `0x${SEPOLIA_CHAIN_ID.toString(16)}`,
                chainName: "Sepolia Test Network",
                rpcUrls: ["https://rpc.sepolia.org"],
                nativeCurrency: {
                  name: "Sepolia Ether",
                  symbol: "SEP",
                  decimals: 18
                },
                blockExplorerUrls: ["https://sepolia.etherscan.io"]
              }
            ],
          });
          return true;
        } catch (addError) {
          console.error('Failed to add Sepolia network:', addError);
        }
      } else {
        console.error('Failed to switch to Sepolia network:', switchError);
      }
    }
    return false;
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="placeholder"></div>
          <h1 className="logo-container">
            <img 
              src="/gpex_logo_whtie_1000px.webp" 
              alt="GPEX Token Exchange" 
              className="logo" 
              style={{
                width: '200px',
                height: 'auto'
              }}
            />
          </h1>
          <div className="wallet-buttons">
            {active ? (
              <>
                <button className="wallet-address-button">
                  {account.substring(0, 4)}...{account.substring(account.length - 4)}
                </button>
                <button onClick={disconnect} className="disconnect-button">
                  Disconnect
                </button>
                {chainId !== SEPOLIA_CHAIN_ID && (
                  <button onClick={switchToSepolia} className="switch-network-button">
                    Switch to Sepolia
                  </button>
                )}
              </>
            ) : (
              <button onClick={connect} className="connect-wallet-button">
                Connect Wallet
              </button>
            )}
          </div>
        </div>
      </header>
      <main className="content-container">
        <TokenExchange switchToSepolia={switchToSepolia} />
      </main>
    </div>
  );
}

export default App;
