import React from 'react';
import { useWeb3React } from '@web3-react/core';
import { InjectedConnector } from '@web3-react/injected-connector';
import TokenExchange from './components/TokenExchange';
import './App.css';

const injected = new InjectedConnector({
  supportedChainIds: [1, 3, 4, 5, 42, 56, 97] // 필요한 네트워크 ID만 남기세요
});

function App() {
  const { active, account, activate, deactivate } = useWeb3React();

  async function connect() {
    try {
      await activate(injected);
    } catch (ex) {
      console.error('Failed to connect:', ex);
    }
  }

  async function disconnect() {
    try {
      deactivate();
    } catch (ex) {
      console.error('Failed to disconnect:', ex);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="placeholder"></div>
          <h1 className="logo-container">
            <img 
              src="/gpex_logo_whtie_1000px.webp" 
              alt="GPEX Token Exchange" 
              className="logo" 
              style={{
                width: '200px',
                height: 'auto'
              }}
            />
          </h1>
          <div className="wallet-buttons">
            {active ? (
              <>
                <button className="wallet-address-button">
                  {account.substring(0, 4)}...{account.substring(account.length - 4)}
                </button>
                <button onClick={disconnect} className="disconnect-button">
                  Disconnect
                </button>
              </>
            ) : (
              <button onClick={connect} className="connect-wallet-button">
                Connect Wallet
              </button>
            )}
          </div>
        </div>
      </header>
      <main className="content-container">
        <TokenExchange />
      </main>
    </div>
  );
}

export default App;
